con:mockOperation.con:dispatchPath:
import javax.xml.xpath.*
import groovy.xml.StreamingMarkupBuilder
import groovy.lang.Binding;
import groovy.lang.Script;
def util = new com.eviware.soapui.support.GroovyUtils( context )
def xml = new XmlSlurper().parseText(mockRequest.requestContent)
def country = xml.Body.CapitalCity.sCountryISOCode
def str = country.toString()
log.info str
if (str == 'IN' || str == 'In' ||str == 'in')
{
context.CaptialCity = "New Delhi"
}
else if(str == 'GB'|| str == 'gb' || str == 'Gb')
{
context.CaptialCity = "London"
}

else 
{
context.CaptialCity = "Unknown"
}

con:response.con:script:
import javax.xml.xpath.*
import groovy.xml.StreamingMarkupBuilder
import groovy.lang.Binding;
import groovy.lang.Script;
def util = new com.eviware.soapui.support.GroovyUtils( context )
def xml = new XmlSlurper().parseText(mockRequest.requestContent)
def country = xml.Body.CountryCurrency.sCountryISOCode
def str = country.toString()
log.info str
if (str == 'IN' || str == 'In' ||str == 'in')
{
context.ISOCode = "INR"
context.Name = "RUPEES"

}
else if(str == 'US'|| str == 'us' || str == 'Us')

{
context.ISOCode = "USD"
context.Name = "DOLLARS"

}


else if(str == 'FR'|| str == 'fr' || str == 'Fr')

{
context.ISOCode = "EUR"
context.Name = "EURO"

}



else 
{
context.ISOCode = "Unknown ISOCode"
context.Name = "Unknown Name"
}
